import React from "react";
import { ResponsiveBullet } from '@nivo/bullet'
import { makeStyles } from '@material-ui/core/styles';
import { Card, CardContent, Typography } from "@material-ui/core";


const data = [
  {
    "id": "temp.",
    "ranges": [
      24,
      76,
      15,
      0,
      120
    ],
    "measures": [
      67
    ],
    "markers": [
      104
    ]
  },

]


function MyResponsiveBullet(){

  const useStyles = makeStyles({
    root: {
      maxWidth: 600,
      minWidth: 290,
      height: 150,
      marginBottom: 50,
      //paddingBottom: 80
      background: '#3f3b3b',
      color: 'white',
      opacity: 0.90
    }
  })
  const styles = useStyles();

  return (
    <Card className={styles.root} elevation={6}>
    <CardContent><Typography variant="h5">Nutritional comparison</Typography></CardContent>
    <ResponsiveBullet
        data={data}
        margin={{ right: 50, bottom: 100, left: 50 }}
        spacing={46}
        titleAlign="start"
        titleOffsetX={-70}
        measureSize={0.2}
        rangeColors="nivo"
        animate={true}
        motionStiffness={90}
        motionDamping={12}
    />
    </Card>
  )
}

export default MyResponsiveBullet;
