import React, {useState} from "react";
import { Input, Typography, Button, Card, Paper, Grid, createMuiTheme, ThemeProvider, createGenerateClassName } from "@material-ui/core";
import styles from "./App.module.css";
import Header from "./Components/Header";
import { makeStyles, useTheme } from "@material-ui/core/styles";
import SearchBox from "./Components/SearchBox";
import Table from "./Components/Table";
import ImgMediaCard from "./Components/ImgMediaCard";
import Chart from "./Components/Chart";

import getData from "./Functionality/api";
import dataTrasform from "./Functionality/data";

//utility function 
function isNotEmpty(obj) {
  for(var key in obj) {
      if(obj.hasOwnProperty(key))
          return true;
  }
  return false;
}


function App() {

  /* FUNCTIONALITY */

  /* THEME */

  const [darkMode, setDarkMode] = useState(true); 
  const theme = createMuiTheme({
    palette: {
      type: darkMode ? "dark" : "light",
    },
  });

  const useStyles = makeStyles({
    root: {
      margin: 0,
      padding: 0,
      height: "100%",
      background: "linear-gradient(45deg, #FE6B8B 30%, #FF8E53 90%)",
    },
  });

  const classes = useStyles();


  return(
    <ThemeProvider theme={theme}>
      <Paper className={classes.root} square={true} elevation={0} height="100%">
        <Grid container direction="column" spacing={5}>

          <Grid item>
            <Header 
              darkMode={darkMode}
              setDarkMode={setDarkMode}
            />
          </Grid>

          <Grid item container justify="center">
            <SearchBox />
          </Grid>

          <Grid item container spacing={4} justify="center">
            <Grid item xs={10} sm={8} md={5}>
              <ImgMediaCard />
            </Grid>
            <Grid item xs={10} sm={8} md={5}>
              <Chart />            
            </Grid>
              </Grid>

        </Grid>
      </Paper>
    </ThemeProvider>
  )
};

export default App;
