import React from "react"
import { ResponsiveBar } from '@nivo/bar'
import { makeStyles } from '@material-ui/core/styles';
import { Card, CardContent, Typography } from "@material-ui/core";


function MyResponsiveBar(props) {

  const useStyles = makeStyles({
    root: {
      maxWidth: 600,
      minWidth: 290,
      height: 150,
      marginBottom: 10,
      paddingBottom: 10,
      background: '#3f3b3b',
      paddingTop: 0,
      color: 'white',
      opacity: 0.90
    }
  })
  const styles = useStyles();

  const data = [
    {
      "country": "AD",
      "hot dog": 37,
      "hot dogColor": "hsl(72, 70%, 50%)",
      "burger": 159,
      "burgerColor": "hsl(29, 70%, 50%)",
      "sandwich": 169,
      "sandwichColor": "hsl(119, 70%, 50%)",
      "kebab": 15,
      "kebabColor": "hsl(136, 70%, 50%)",
      "fries": 148,
      "friesColor": "hsl(327, 70%, 50%)",
      "donut": 180,
      "donutColor": "hsl(349, 70%, 50%)"
    }]

  return (
    <Card className={styles.root} elevation={6}>
    <CardContent><Typography variant="h5">Nutritional comparison</Typography></CardContent>
    <ResponsiveBar
        data={data}
        keys={[ 'hot dog', 'burger', 'sandwich', 'kebab', 'fries', 'donut' ]}
        indexBy="country"
        margin={{ top: 0, right: 30, bottom: 100, left: 30 }}
        padding={0.3}
        layout="horizontal"
        colors={{ scheme: 'nivo' }}
        defs={[
            {
                id: 'dots',
                type: 'patternDots',
                background: 'inherit',
                color: '#38bcb2',
                size: 4,
                padding: 1,
                stagger: true
            },
            {
                id: 'lines',
                type: 'patternLines',
                background: 'inherit',
                color: '#eed312',
                rotation: -45,
                lineWidth: 6,
                spacing: 10
            }
        ]}
        fill={[
            {
                match: {
                    id: 'fries'
                },
                id: 'dots'
            },
            {
                match: {
                    id: 'sandwich'
                },
                id: 'lines'
            }
        ]}
        borderColor={{ from: 'color', modifiers: [ [ 'brighter', 1.6 ] ] }}
        axisTop={null}
        axisRight={null}
        axisBottom={{
            tickSize: 5,
            tickPadding: 5,
            tickRotation: 0,
            legend: 'country',
            legendPosition: 'middle',
            legendOffset: 32
        }}
        axisLeft={{
            tickSize: 5,
            tickPadding: 5,
            tickRotation: 0,
            legend: 'food',
            legendPosition: 'middle',
            legendOffset: -40
        }}
        labelSkipWidth={12}
        labelSkipHeight={12}
        labelTextColor={{ from: 'color', modifiers: [ [ 'brighter', 1.6 ] ] }}

        animate={true}
        motionStiffness={90}
        motionDamping={15}
    />
    </Card>
  )
}

export default MyResponsiveBar;
