import React from "react"
import { ResponsiveBar } from '@nivo/bar'
import { makeStyles } from '@material-ui/core/styles';
import { Card, CardContent, Typography } from "@material-ui/core";


function MyResponsiveBar(props) {

  const useStyles = makeStyles({
    root: {
      maxWidth: 600,
      minWidth: 290,
      height: 400,
      marginBottom: 10,
      paddingBottom: 80
    }
  })

  const lineGraphSettings = {
    theme: {
    fontSize: '14px',
    textColor: props.color,
    itemTextColor: props.color
    },
    };

  //array of the current foods 
  function getFoods(data){
    return Object.keys(data).filter(el => el!=="nutrient"&&el!=="max")
  }
  const currentFoods = getFoods(props.data[0])

  const styles = useStyles();

  return (
    <Card className={styles.root} elevation={6}>
    <CardContent><Typography variant="h5">Nutritional comparison</Typography></CardContent>
    <ResponsiveBar
        data={props.data}
        keys={currentFoods}
        indexBy="nutrient"
        margin={{ top: 50, right: 130, bottom: 50, left: 60 }}
        padding={0.3}
        colors={{ scheme: 'nivo' }}
        defs={[
            {
                id: 'dots',
                type: 'patternDots',
                background: 'inherit',
                color: '#38bcb2',
                size: 4,
                padding: 1,
                stagger: true
            },
            {
                id: 'lines',
                type: 'patternLines',
                background: 'inherit',
                color: '#eed312',
                rotation: -45,
                lineWidth: 6,
                spacing: 10
            }
        ]}
        fill={[
            {
                match: {
                    id: currentFoods[4]
                },
                id: 'dots'
            },
            {
                match: {
                    id: currentFoods[2]
                },
                id: 'lines'
            }
        ]}
        borderColor={{ from: 'color', modifiers: [ [ 'darker', 1.6 ] ] }}
        axisTop={null}
        axisRight={null}
        axisBottom={{
            tickSize: 7,
            tickPadding: 5,
            tickRotation: 0,
            legend: 'Nutrients',
            legendPosition: 'middle',
            legendOffset: 45
        }}
        axisLeft={{
            tickSize: 7,
            tickPadding: 5,
            tickRotation: 0,
            legend: 'food',
            legendPosition: 'middle',
            legendOffset: -40
        }}
        labelSkipWidth={12}
        labelSkipHeight={12}
        labelTextColor={{ from: 'color', modifiers: [ [ 'darker', 1.6 ] ] }}
        legends={[
            {
                dataFrom: 'keys',
                anchor: 'bottom-right',
                direction: 'column',
                justify: false,
                translateX: 120,
                translateY: 0,
                itemsSpacing: 2,
                itemWidth: 100,
                itemHeight: 20,
                itemDirection: 'left-to-right',
                itemOpacity: 0.85,
                symbolSize: 20,
                itemTextColor: props.color,
                effects: [
                    {
                        on: 'hover',
                        style: {
                            itemOpacity: 1,
                        }
                    }
                ]
            }
        ]}
        enableGridY={true}
        animate={true}
        motionStiffness={90}
        motionDamping={15}
        theme={lineGraphSettings.theme}
    />
    </Card>
  )
}

export default MyResponsiveBar;
