import { makeStyles } from '@material-ui/core/styles';
import { Card, CardContent, CardActions, CardActionArea, Button } from "@material-ui/core";
import React, { useState, useEffect } from 'react';
import {
  Radar, RadarChart, PolarGrid, Legend,
  PolarAngleAxis, PolarRadiusAxis, ResponsiveContainer, XAxis, Label
} from 'recharts';


// example of data format 
/*
const realData = [
  {nutrient: "calories", apple: 52, banana: 50, kiwi: 61, max: 89},
  {nutrient: "fat", apple: 0.17, banana: 0.33, kiwi: 50.52, max: 0.52},
  {nutrient: "carbs", apple: 13.81, banana: 22.84, kiwi: 74.66, max: 22.84},
  {nutrient: "proteins", apple: 0.26, banana: 81.09, kiwi: 1.14, max: 1.14},
  {nutrient: "fiber", apple: 2.4, banana: 82.6, kiwi: 3, max: 3}
]
*/


const useStyles = makeStyles({
  root: {
    maxWidth: 600,
    minWidth: 270,
    height: 500
  }
})


function MyRadarChart(props) {
    const styles = useStyles();
    const fills = ["#8884d8", "#82ca9d", "#edb2c2"];

    //array of the current foods 
    function getFoods(data){
      return Object.keys(data).filter(el => el!=="nutrient"&&el!=="max")
    }
    const currentFoods = getFoods(props.data[0])
    //let foodOpacities = [];
    //currentFoods.forEach(food => foodOpacities.push([food, 0.6]))
    //foodOpacities = Object.fromEntries(foodOpacities)
    //console.log(foodOpacities)

    const [opacity, setOpacity] = useState(0.6);
    const [opacities, setOpacities] = useState({})

    useEffect(() => {
      const currentFoods = getFoods(props.data[0])
      let foodOpacities = [];
      currentFoods.forEach(food => foodOpacities.push([food, 0.6]))
      foodOpacities = Object.fromEntries(foodOpacities)
      setOpacities(foodOpacities)
    }, [])



    function handleLegendClick(e){
      console.log(e)
      //setOpacity(1);
    }

    const ops = {
      banana: 0.5,
      avocado: 0.6
    }



    return (    
      <Card className={styles.root} elevation={6}>
        <ResponsiveContainer width="99%" height="100%">
            <RadarChart outerRadius={"70%"}  data={props.data} >
              <PolarGrid />
              <PolarAngleAxis dataKey="nutrient" />
              <PolarRadiusAxis angle={30} domain={[0, 20]} />

              {currentFoods.map((food, id) => (
                <Radar name={food} dataKey={food} fill={fills[id]} fillOpacity={ops.food} />))}
r
            {/* 
              <Radar name="Apple" dataKey="apple" fill="#8884d8" fillOpacity={0.6} />
              <Radar name="Banana" dataKey="banana" fill="#82ca9d" fillOpacity={0.6} />
              <Radar name="Kiwi" dataKey="kiwi" fill="#edb2c2" fillOpacity={0.6} />
            */} 
              <Legend onClick={handleLegendClick}/>
            </RadarChart> 
        </ResponsiveContainer>        
      </Card>

    );
  }



export default MyRadarChart;
