import React from "react"
import { ResponsiveBar } from '@nivo/bar'
import { makeStyles } from '@material-ui/core/styles';
import { Card, CardContent, Typography } from "@material-ui/core";


function MyResponsiveBar(props) {

  const useStyles = makeStyles({
    root: {
      maxWidth: 600,
      minWidth: 290,
      height: 500,
      marginBottom: 10,
      //paddingBottom: 80
      background: '#3f3b3b',
      color: 'white',
      opacity: 0.90
    }
  })

  const lineGraphSettings = {
    theme: {
    fontSize: '14px',
    textColor: props.color,
    itemTextColor: props.color
    },
    };

  //array of the current foods 
  function getFoods(data){
    return Object.keys(data).filter(el => el!=="nutrient"&&el!=="max")
  }
  const currentFoods = getFoods(props.data[0])

  const styles = useStyles();

  const fills = ["#f35588", "#71a95a", "#14b1ab", "#e8505b", "#f9d56e"];
  const colors = { 
    [currentFoods[0]]: "#DB5375", 
    [currentFoods[1]]: "#EC9192", 
    [currentFoods[2]]: "#f8dc88", 
    [currentFoods[3]]: "#B5BD89",
    [currentFoods[4]]: "#729EA1",
  }
  const getColor = bar => colors[bar.id]

  const data = [
    {
      "country": "AD",
      "hot dog": 37,
      "hot dogColor": "hsl(72, 70%, 50%)",
      "burger": 159,
      "burgerColor": "hsl(29, 70%, 50%)",
      "sandwich": 169,
      "sandwichColor": "hsl(119, 70%, 50%)",
      "kebab": 15,
      "kebabColor": "hsl(136, 70%, 50%)",
      "fries": 148,
      "friesColor": "hsl(327, 70%, 50%)",
      "donut": 180,
      "donutColor": "hsl(349, 70%, 50%)"
    }]

  return (
    <Card className={styles.root} elevation={6}>
    <CardContent><Typography variant="h5">Nutritional comparison</Typography></CardContent>
    <ResponsiveBar
        data={data}
        keys={[ 'hot dog', 'burger', 'sandwich', 'kebab', 'fries', 'donut' ]}
        indexBy="country"
        margin={{ top: 50, right: 130, bottom: 50, left: 60 }}
        padding={0.3}
        layout="horizontal"
        colors={{ scheme: 'nivo' }}
        defs={[
            {
                id: 'dots',
                type: 'patternDots',
                background: 'inherit',
                color: '#38bcb2',
                size: 4,
                padding: 1,
                stagger: true
            },
            {
                id: 'lines',
                type: 'patternLines',
                background: 'inherit',
                color: '#eed312',
                rotation: -45,
                lineWidth: 6,
                spacing: 10
            }
        ]}
        fill={[
            {
                match: {
                    id: 'fries'
                },
                id: 'dots'
            },
            {
                match: {
                    id: 'sandwich'
                },
                id: 'lines'
            }
        ]}
        borderColor={{ from: 'color', modifiers: [ [ 'darker', 1.6 ] ] }}
        axisTop={null}
        axisRight={null}
        axisBottom={{
            tickSize: 5,
            tickPadding: 5,
            tickRotation: 0,
            legend: 'country',
            legendPosition: 'middle',
            legendOffset: 32
        }}
        axisLeft={{
            tickSize: 5,
            tickPadding: 5,
            tickRotation: 0,
            legend: 'food',
            legendPosition: 'middle',
            legendOffset: -40
        }}
        labelSkipWidth={12}
        labelSkipHeight={12}
        labelTextColor={{ from: 'color', modifiers: [ [ 'darker', 1.6 ] ] }}
        legends={[
            {
                dataFrom: 'keys',
                anchor: 'bottom-right',
                direction: 'column',
                justify: false,
                translateX: 120,
                translateY: 0,
                itemsSpacing: 2,
                itemWidth: 100,
                itemHeight: 20,
                itemDirection: 'left-to-right',
                itemOpacity: 0.85,
                symbolSize: 20,
                effects: [
                    {
                        on: 'hover',
                        style: {
                            itemOpacity: 1
                        }
                    }
                ]
            }
        ]}
        animate={true}
        motionStiffness={90}
        motionDamping={15}
    />
    </Card>
  )
}

export default MyResponsiveBar;
